import styled from "styled-components";
// import { Modal } from "./Modal.css";
import $jQ from "jquery";

const ModalWrap = styled.div`
  width: 500px;
  height: 250px;
  background-color: #333;
  border: 3px solid #00d1fe;
  border-radius: 8px;
`;
const CancelWrap = styled.div`
  width: 100%;
  height: 25px;
  background-color: #00d1fe;
  display: flex;
  justify-content: space-between;
  span {
    font-weight: bold;
  }
  button {
    width: 22.5px;
    height: 22.5px;
    display: flex;
    align-items: center;
    text-align: center;
    font-weight: bold;
    background-color: #333;
    border-radius: 4px;
    color: #00d1fe;
    margin-right: 2px;
    cursor: pointer;
    padding: 3px 0 0 4px;
  }
`;

const ModalContents = styled.div`
  width: 100%;
  height: calc(100% - 25px);
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-around;
  span {
    width: calc(100% - 60px);
    font-size: 20px;
    color: #00d1fe;
    padding: 0 30px;
    height: auto;
    text-align: center;
  }
`;
const BtnWrap = styled.div`
  width: 100%;
  display: flex;
  justify-content: end;
  height: auto;
  a {
    width: 90px;
    height: 30px;
    background-color: #333;
    border: 1px solid #00d1fe;
    color: #00d1fe;
    margin-right: 20px;
    border-radius: 4px;
    p {
      width: calc(100% - 5px);
      height: calc(100% - 5px);
      margin: 2px;
      justify-content: center;
      display: flex;
      align-items: center;
      cursor: pointer;
      border-radius: 4px;
      &:active {
        border: 1px dotted #00d1fe;
      }
    }
  }
`;

export function Modal() {
  return (
    <>
      {/* <Modal /> */}
      {/* 모달 창 시작 */}
      <div className="alertPop alert">
        <ModalWrap>
          <CancelWrap>
            <span>로그인 오류</span>
            <button>X</button>
          </CancelWrap>
          <ModalContents>
            <span>ID와 비밀번호를 입력하세요!</span>
            <BtnWrap>
              <a>
                <p>확인</p>
              </a>
              <a>
                <p>취소</p>
              </a>
            </BtnWrap>
          </ModalContents>
        </ModalWrap>
      </div>
      {/* 모달 창 끝 */}
    </>
  );
}

// 상황에 따른 모달 내용 변경
const CancelText = CancelWrap.firstElementChild;
const Xbtn = CancelWrap.lastElementChild;
Xbtn.classList.add("closebtn");
const ModalContentsSpan = ModalContents.firstElementChild;

const Yes = BtnWrap.firstElementChild;
Yes.classList.add("yes");
const No = BtnWrap.lastElementChild;
No.classList.add("no");

// Login관련 모달 시작
export function IDIsNull() {
  $jQ(".alert").addClass("active");
  $jQ(".closebtn").click(function () {
    $jQ(".alert").removeClass("active");
  });
  $jQ(".yes").click(function () {
    $jQ(".alert").removeClass("active");
  });
  $jQ(".no").click(function () {
    $jQ(".alert").removeClass("active");
  });
}
