import styled from "styled-components";
import "./FAQ.css";
import axios from "axios";

const FAQTitle = styled.div`
  width: calc(100% - 400px);
  margin: 50px auto;
  font-family: "GmarketSansMedium";
  text-align: center;
  height: calc(15% - 100px);
`;
const FAQContainer = styled.div`
  width: calc(100% - 400px);
  border-top: 3px solid #00d1fe;
  height: auto;
  margin: 0 auto;
`;
const FAQWrap = styled.div`
  display: flex;
  flex-direction: column;
  height: 500px;
  overflow: scroll;
  overflow-x: hidden;
  ul {
    height: auto;
    border-bottom: 1px solid #00d1fe;
    padding: 0px 50px;
    cursor: pointer;
    display: flex;
    justify-content: space-around;
    list-style: none;
    align-items: center;
    height: 60px;
    li:first {
      width: 50px;
      text-align: center;
      margin-right: 5px;
    }
    li:nth-child(2) {
      margin-right: 5px;
    }
    li {
      width: 30%;
      height: auto;
      font-size: 20px;
      font-family: "GmarketSansMedium";
      text-align: center;
    }
  }
  ul:first {
    display: none;
  }
  &:hover {
    color: #bf1369;
  }
  tdody {
    tr {
      td:last-child {
        text-align: right;
      }
    }
  }
`;

export function FAQ() {
  async function fetchFAQs() {
    try {
      const response = await axios.get("http://localhost:8080/faq");
      const FAQData = response.data;
      renderFAQs(FAQData);
    } catch (error) {
      console.log("FAQ 호출 실패", error);
    }
  }
  function renderFAQs(faqs) {
    FAQWrap.innerHTML = "";
    faqs.forEach((faq) => {
      const faqItem = document.createElement("div");
      faqItem.classList.add("FAQ-item");

      const question = document.createElement("div");
      question.classList.add("question");
      question.textContent = faq.question;
      question.addEventListener("click", () => {
        answer.classList.toggle("hidden");
      });

      const answer = document.createElement("div");
      answer.classList.add("answer", "hidden");
      answer.textContent = faq.answer;

      faqItem.appendChild(question);
      faqItem.appendChild(answer);
      FAQWrap.appendChild(faqItem);
    });
  }
  fetchFAQs();
  return (
    <>
      <FAQTitle>FAQ</FAQTitle>
      <FAQContainer>
        <FAQWrap></FAQWrap>
      </FAQContainer>
    </>
  );
}
